    let token;
    while ((token = this.expect("<", ">", "<=", ">="))) {
      left = {
        type: ASTType.BinaryExpression,
        operator: /** @type {import("../lexer/lexer.js").Token} */ (token).text,
        left,
        right: this.additive(),
      };
    }
    return left;
